import React, { useState } from "react"
import logo from "../images/logo.png"

export const LoginWindow = () => {
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")

    const login_button_clicked = async() => {
        const response = await try_login();
        const response_text = await response.text();
        if (response.status === 200) {
            document.cookie = response_text;
            // location.href = "/homepage.html";
        }
        else if(response.status === 404){
            alert("Email doesn't exist");
        }
        else if(response.status === 401){
            alert("Wrong password");
        }
        else if(response.status === 403){
            alert("User needs to be approved by admin");
        }
    }

    const try_login = async() => {
        try{
            const promise = fetch('/api/users/login',
            {
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                method: 'POST',
                body: JSON.stringify({email: email, password: password})
            })
            return promise;
        }
        catch(err){
            console.log(err);
        }
	}

    return(
        <div>
            <img src={logo} alt="logo" id="site_logo"></img>
            <h1 className="center_elem">Login</h1>
            <table className="medium_window center_elem border">
                <tbody>
                    <tr>
                        <td><label>Email:</label></td><td><input type="text" value={email} onChange={(newValue) => { setEmail(newValue.target.value)}}/></td>
                    </tr>
                    <tr>
                        <td><label>Password:</label></td><td><input type="password" value={password} onChange={(newValue) => { setPassword(newValue.target.value)}}/></td>
                    </tr>
                    <tr>
                        <td colSpan="2"><label>Don't have an account? <a href="/register.html">Register</a></label></td>
                    </tr>
                    <tr>
                        <td colSpan="2"><button onClick={login_button_clicked}>Login</button></td>
                    </tr>
                </tbody>
            </table>
        </div>
    )
}

