import React from "react";
import { NavBarItem } from "./NavBarItem";

class NavBar extends React.Component {
	constructor(props) {
		super(props);
        this.state = {
            normal_pages: [
                {name: "About", url: "about.html"},
                {name: "Home", url: "homepage.html"},
                {name: "Messages", url: "messages.html"}
            ],
            full_pages: [
                {name: "About", url: "about.html"},
                {name: "Home", url: "homepage.html"},
                {name: "Messages", url: "messages.html"},
                {name: "Admin Console", url: "admin.html"}
            ]
        }
	}

	render() {
        let pages_list;
        if(this.props.mode == "full"){
            pages_list = this.state.full_pages.map((item, index) =>{
                return <li key={index}><NavBarItem name={item.name} url={item.url}></NavBarItem></li>
            })
        }
        else if(this.props.mode == "normal"){
            pages_list = this.state.normal_pages.map((item, index) =>{
                return <li key={index}><NavBarItem name={item.name} url={item.url}></NavBarItem></li>
            })
        }

		return(
            <div id="navbar">
                <ul>
                    {pages_list}
                </ul>
            </div>
		)
	}
}

export default NavBar;
