import React, {useEffect, useState} from 'react';

export const AboutPage = () => {
	const [user, setUser] = useState({});

	useEffect(() => {
		
	})
	return (
		<div>
            <h1 className="center_elem">About</h1>
            <table className="medium_window center_elem border">
            	<tbody>
            	    <tr>
                       <td><label>Full Name:</label></td><td><label>{user.full_name}</label></td>
    	            </tr>
    	            <tr>
        	        	<td><label>Username:</label></td><td><label>{user.username}</label></td>
            	    </tr>
            	    <tr>
 						<td><label>ID:</label></td><td><label>{user.id}</label></td>
            	    </tr>
            	    <tr>
 						<td><label>Role:</label></td><td><label>{user.role}</label></td>
            	    </tr>
                    <tr>
 						<td><label>Joined:</label></td><td><label>{user.creation_date}</label></td>
            	    </tr>
            	</tbody>
            </table>
        </div>
	)
}

