class About extends React.Component {
	constructor(props) {
		super(props);
		this.state = {
            user: {},
			user_token: extract_token_from_cookie()
        }
	}

	async componentDidMount() {
		const user_permissions = await verify_page_access();
		if(user_permissions.includes("admin")){
			this.setState({navbar_mode: "full"});
		}
		else if(user_permissions.includes("user")){
			this.setState({navbar_mode: "normal"});
		}

        const user = await this.get_user_details();
		this.setState({user: user});
	}

    async get_user_details() {
		const response = await fetch('/api/users/about', 
		{
			headers: {
				'Accept': 'application/json',
				'Content-Type': 'application/json',
                'authorization': this.state.user_token
			},
			method: 'GET'
		});
		if (response.status != 200) throw new Error('Error while fetching user details');
		const data = await response.json();
		return data;
	}

	render() {
		return(
			<div>
				<NavBar mode={this.state.navbar_mode}></NavBar>
                <h1 className="center_elem">About</h1>
                <table className="medium_window center_elem border">
                    <tbody>
                        <tr>
                            <td><label>Full Name:</label></td><td><label>{this.state.user.full_name}</label></td>
                        </tr>
                        <tr>
                            <td><label>Username:</label></td><td><label>{this.state.user.username}</label></td>
                        </tr>
                        <tr>
						<td><label>ID:</label></td><td><label>{this.state.user.id}</label></td>
                        </tr>
                        <tr>
						<td><label>Role:</label></td><td><label>{this.state.user.role}</label></td>
                        </tr>
                        <tr>
							<td><label>Joined:</label></td><td><label>{this.state.user.creation_date}</label></td>
                        </tr>
                    </tbody>
                </table>
            </div>
		)
	}
}
