import React, { useEffect } from 'react'
import { Routes, Route } from "react-router-dom"
import logo from "../../images/logo.png"
import { LoginPage } from '../login/LoginPage'
import { NavBar } from './NavBar'
import { HomePage } from '../HomePage'
import { ProfilePage } from '../profile/ProfilePage'
import { LibraryPage } from '../library/LibraryPage'
import { StorePage } from '../store/StorePage'
import { FriendsPage } from '../friends/FriendsPage'

export const MainPagesContainer = (props) => {

  // Redirect to login page if no user token found
  useEffect(() => {
		if(!sessionStorage.getItem("userToken")){
			window.location.href = "/login";
		}
	}, [])

    const deleteToken = () => {
      sessionStorage.removeItem("userToken");
    }

    const pages = [{
      name: "Home",
      relativePath: "/home",
      element: <HomePage />
    },{
      name: "Store",
      relativePath: "/store",
      element: <StorePage />
    },{
      name: "Library",
      relativePath: "/library",
      element: <LibraryPage />
    },{
      name: "Friends",
      relativePath: "/friends",
      element: <FriendsPage />
    },{
      name: "Profile",
      relativePath: "/profile",
      element: <ProfilePage />
    },{
      name: "Logout",
      relativePath: "/login",
      element: <LoginPage />,
      extra_fn: deleteToken
    }]

    const routes = pages.map((page, index) => <Route path={page.relativePath} element={page.element} extra_fn={page.extra_fn} key={index} />);

    return(
      <>
        <img src={logo} alt="logo" id="site_logo"></img>
        <NavBar pages={pages} />
        <Routes>
          {routes}
        </Routes>
      </>
    )
  }
