class RegisterPage extends React.Component {
	constructor(props) {
		super(props);
        this.state = {
            name: "",
            id: "",
            email: "",
            password: ""
        }
		this.handle_create_click = this.handle_create_click.bind(this);
        this.handle_name_change = this.handle_name_change.bind(this);
        this.handle_id_change = this.handle_id_change.bind(this);
        this.handle_email_change = this.handle_email_change.bind(this);
        this.handle_password_change = this.handle_password_change.bind(this);
	}

    handle_name_change(event){
        this.setState({name: event.target.value})
    }

    handle_id_change(event){
        this.setState({id: event.target.value})
    }

    handle_email_change(event){
        this.setState({email: event.target.value})
    }

    handle_password_change(event){
        this.setState({password: event.target.value})
    }

    async handle_create_click(){
        const response = await this.create_user();
        const response_text = await response.text();
        alert(response_text);
        if (response.status == 201) {
            location.href = "/login.html";
        }
    }

    async create_user() {
        try{
            const promise = fetch('/api/users',
            {
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json'
                },
                method: 'POST',
                body: JSON.stringify({full_name: this.state.name, id: this.state.id, email: this.state.email, password: this.state.password})
            })
            return promise;
        }
        catch(err){
            console.log(err);
        }
	}

	render() {
		return(
            <div>
                <img src="../images/logo.png" id="site_logo"></img>
                <h1 className="center_elem">Register</h1>
                <table className="medium_window center_elem border">
                    <tbody>
                        <tr>
                            <td><label>Full Name:</label></td><td><input type="text" value={this.state.name} onChange={this.handle_name_change}></input></td>
                        </tr>
                        <tr>
                            <td><label>Id:</label></td><td><input type="text" value={this.state.id} onChange={this.handle_id_change}></input></td>
                        </tr>
                        <tr>
                            <td><label>Email:</label></td><td><input type="text" value={this.state.email} onChange={this.handle_email_change}></input></td>
                        </tr>
                        <tr>
                            <td><label>Password:</label></td><td><input type="password" value={this.state.password} onChange={this.handle_password_change}></input></td>
                        </tr>
                        <tr>
                            <td colSpan="2"><button onClick={this.handle_create_click}>Create</button></td>
                        </tr>
                    </tbody>
                </table>
            </div>
        )
	}
}
